pragma solidity >=0.4.22 <0.8.0;

contract BookStore {
    address sender;
    string fullName;
    string description;
    uint256 price;
    uint256 totalBook;
  

  constructor() public{
      totalBook = 10000000;
  }
  function sellBook(string memory _fullName, string memory _description, uint256 _price) public  {
      sender = msg.sender;
      fullName = _fullName;
      description = _description;
      price = _price;

  }
  function getBook() public view returns(address _sender,string memory _fullName, string memory _description, uint256 _price) {
      return (sender,fullName,description,price);
  }
}
